import React, { Component } from "react";
import Web3 from "web3";
//Need to correct RenJS tutorial BTC/ETH imports with newest NPM file hyphen
import RenJS from "@renproject/ren";
import { Bitcoin } from "@renproject/chains-bitcoin";
import { Ethereum } from "@renproject/chains-ethereum";

import logo from "../logo.png";
import "./App.css";
import Marketplace from "../abis/Marketplace.json";
import ABI from "../abis/ABI.json";
import Header from "./Navbar.js";
import Main from "./Main";


// class dapp extends React.Component {
//   render() {
//     return  (
//       <div className="bridge">test
//       <Renapp /></div>
//     )
//   }
// }

class Store extends Component {
  async componentWillMount() {
    await this.loadWeb3();
    await this.loadBlockchainData();
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum);
      await window.ethereum.enable();
    } else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider);
    } else {
      window.alert(
        "Non-Ethereum browser detected. You should consider trying MetaMask!"
      );
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3;
    // Load account
    const accounts = await web3.eth.getAccounts();
    this.setState({ account: accounts[0] });
    const networkId = await web3.eth.net.getId();
    const networkData = Marketplace.networks[networkId];
    if (networkData) {
      const marketplace = web3.eth.Contract(
        Marketplace.abi,
        networkData.address
      );
      this.setState({ marketplace });
      const productCount = await marketplace.methods.productCount().call();
      this.setState({ productCount });
      // Load products
      for (var i = 1; i <= productCount; i++) {
        const product = await marketplace.methods.products(i).call();
        this.setState({
          products: [...this.state.products, product],
        });
      }
      this.setState({ loading: false });
    } else {
      window.alert("Marketplace contract not deployed to detected network.");
    }
  }

  constructor(props) {
    super(props);
    this.state = {
      account: "",
      productCount: 0,
      products: [],
      loading: true,
    };

    this.createProduct = this.createProduct.bind(this);
    this.purchaseProduct = this.purchaseProduct.bind(this);
  }

  createProduct(name, price) {
    this.setState({ loading: true });
    this.state.marketplace.methods
      .createProduct(name, price)
      .send({ from: this.state.account })
      .once("receipt", (receipt) => {
        this.setState({ loading: false });
      });
  }

  purchaseProduct(id, price) {
    this.setState({ loading: true });
    this.state.marketplace.methods
      .purchaseProduct(id)
      .send({ from: this.state.account, value: price })
      .once("receipt", (receipt) => {
        this.setState({ loading: false });
      });
  }

  render() {
    return (
      <div className="App">
        <Header account={this.state.account} />
        <div className="container mt-5"> 
          <div className="row">
            <main role="main" className="col-lg-12 d-flex">
              {this.state.loading ? (
                <div id="loader" className="text-center">
                  <p className="text-center">Loading...</p>
                </div>
              ) : (
                <Main
                  products={this.state.products}
                  createProduct={this.createProduct}
                  purchaseProduct={this.purchaseProduct}
                />
              )}
            </main>
          </div>
        </div>
      </div>
    );
    }
  }

// Replace with your contract's address.
const contractAddress = "0x4EaFf0d9Ce1c01910a1C4AeC87399A4caEc0a0Dd";

class Dapp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      balance: 0,
      message: "",
      error: "",
      renJS: new RenJS("testnet"),
    };
  }

  componentDidMount = async () => {
    let web3Provider;

    // Initialize web3 (https://medium.com/coinmonks/web3-js-ethereum-javascript-api-72f7b22e2f0a)
    // Modern dApp browsers...
    if (window.ethereum) {
      web3Provider = window.ethereum;
      try {
        // Request account access
        await window.ethereum.enable();
      } catch (error) {
        // User denied account access...
        this.logError("Please allow access to your Web3 wallet.");
        return;
      }
    }
    // Legacy dApp browsers...
    else if (window.web3) {
      web3Provider = window.web3.currentProvider;
    }
    // If no injected web3 instance is detected, fall back to Ganache
    else {
      this.logError("Please install MetaMask!");
      return;
    }

    const web3 = new Web3(web3Provider);

    const networkID = await web3.eth.net.getId();
    if (networkID !== 42) {
      this.logError("Please set your network to Kovan.");
      return;
    }

    this.setState({ web3 }, () => {
      // Update balances immediately and every 10 seconds
      this.updateBalance();
      setInterval(() => {
        this.updateBalance();
      }, 10 * 1000);
    });
  };

  render = () => {
    const { balance, message, error } = this.state;
    return (
      <div className="App">
        <p>Balance: {balance} BTC</p>
        <p>
          <button onClick={() => this.deposit().catch(this.logError)}>
            Deposit 0.003 BTC
          </button>
        </p>
        <p>
          <button onClick={() => this.withdraw().catch(this.logError)}>
            Withdraw {balance} BTC
          </button>
        </p>
        <p>{message}</p>
        {error ? <p style={{ color: "red" }}>{error}</p> : null}
      </div>
    );
  };

  updateBalance = async () => {
    const { web3 } = this.state;
    const contract = new web3.eth.Contract(ABI, contractAddress);
    const balance = await contract.methods.balance().call();
    this.setState({ balance: parseInt(balance.toString()) / 10 ** 8 });
  };

  logError = (error) => {
    console.error(error);
    this.setState({ error: String((error || {}).message || error) });
  };

  log = (message) => {
    this.setState({ message });
  };

  deposit = async () => {
    this.logError(""); // Reset error

    const { web3, renJS } = this.state;

    const amount = 0.003; // BTC
    const mint = await renJS.lockAndMint({
      // Send BTC from the Bitcoin blockchain to the Ethereum blockchain.
      asset: "BTC",
      from: Bitcoin(),
      to: Ethereum(web3.currentProvider).Contract({
        // The contract we want to interact with
        sendTo: contractAddress,

        // The name of the function we want to call
        contractFn: "deposit",

        // Arguments expected for calling `deposit`
        contractParams: [
          {
            name: "_msg",
            type: "bytes",
            value: Buffer.from(`Depositing ${amount} BTC`),
          },
        ],
      }),
    });

    // Show the gateway address to the user so that they can transfer their BTC to it.
    this.log(`Deposit ${amount} BTC to ${mint.gatewayAddress}`);

    mint.on("deposit", async (deposit) => {
      // Details of the deposit are available from `deposit.depositDetails`.

      const hash = deposit.txHash();
      const depositLog = (msg) =>
        this.log(`[${hash.slice(0, 8)}][${deposit.status}] ${msg}`);

      await deposit
        .confirmed()
        .on("target", (confs, target) =>
          depositLog(`${confs}/${target} confirmations`)
        )
        .on("confirmation", (confs, target) =>
          depositLog(`${confs}/${target} confirmations`)
        );

      await deposit
        .signed()
        // Print RenVM status - "pending", "confirming" or "done".
        .on("status", (status) => depositLog(`Status: ${status}`));

      await deposit
        .mint()
        // Print Ethereum transaction hash.
        .on("transactionHash", (txHash) => depositLog(`Mint tx: ${txHash}`));
    });

    this.log(`Deposited ${amount} BTC.`);
  };

  withdraw = async () => {
    this.logError(""); // Reset error

    const { web3, renJS, balance } = this.state;

    const recipient = prompt("Enter BTC recipient:");
    const amount = balance;
    const burnAndRelease = await renJS.burnAndRelease({
      // Send BTC from Ethereum back to the Bitcoin blockchain.
      asset: "BTC",
      to: Bitcoin().Address(recipient),
      from: Ethereum(web3.currentProvider).Contract((btcAddress) => ({
        sendTo: contractAddress,

        contractFn: "withdraw",

        contractParams: [
          {
            type: "bytes",
            name: "_msg",
            value: Buffer.from(`Withdrawing ${amount} BTC`),
          },
          {
            type: "bytes",
            name: "_to",
            value: Buffer.from(btcAddress),
          },
          {
            type: "uint256",
            name: "_amount",
            value: RenJS.utils.toSmallestUnit(amount, 8),
          },
        ],
      })),
    });

    let confirmations = 0;
    await burnAndRelease
      .burn()
      // Ethereum transaction confirmations.
      .on("confirmation", (confs) => {
        confirmations = confs;
      })
      // Print Ethereum transaction hash.
      .on("transactionHash", (txHash) => this.log(`txHash: ${String(txHash)}`));

    await burnAndRelease
      .release()
      // Print RenVM status - "pending", "confirming" or "done".
      .on("status", (status) =>
        status === "confirming"
          ? this.log(`${status} (${confirmations}/15)`)
          : this.log(status)
      )
      // Print RenVM transaction hash
      .on("txHash", this.log);

    this.log(`Withdrew ${amount} BTC to ${recipient}.`);
  };
}

